mapper.select:
SELECT id, user_id userId, category, title, content, view_count viewCount, likes_count likesCount, comments_count commentsCount, created_at createdAt FROM board
${keyword} LIKE CONCAT('%', #{search}, '%')
ORDER BY ${orderBy} ${orderDir}

SELECT id, user_id userId, category, title, content, view_count viewCount, likes_count likesCount, comments_count commentsCount, created_at createdAt
FROM board
WHERE id = #{id}

mapper.update:
UPDATE board
SET title = #{title}, content = #{content}, created_at = now()
WHERE id = #{id}

UPDATE board
SET view_count = view_count + 1
WHERE id = #{id}

UPDATE board
SET likes_count = likes_count + 1
WHERE id = #{id}

UPDATE board
SET likes_count = likes_count - 1
WHERE id = #{id}

UPDATE board
SET comments_count = comments_count + 1
WHERE id = #{id}

UPDATE board
SET comments_count = comments_count - 1
WHERE id = #{id}
