mapper.select:
SELECT id, user_id userId, content, reference_id referenceId, reference_type referenceType, created_at createdAt, is_read isRead
FROM notice
WHERE user_id = #{userId}

	SELECT id, user_id userId, content, reference_id referenceId, reference_type referenceType, created_at createdAt, is_read isRead
    FROM notice
	WHERE id = #{id}

mapper.insert:
INSERT INTO gcomment (
    user_id,
    content,
    gnotice_id
) VALUES (
    #{userId},
    #{content},
    #{gnoticeId}
)

mapper.update:
UPDATE gcomment
SET content = #{content}, created_at = now()
WHERE id = #{id}
