mapper.select:
SELECT id, user_id userId, content, reference_id referenceId, reference_type referenceType, created_at createdAt, is_read isRead
FROM notice
WHERE user_id = #{userId}

	SELECT id, user_id userId, content, reference_id referenceId, reference_type referenceType, created_at createdAt, is_read isRead
    FROM notice
	WHERE id = #{id}

mapper.insert:
INSERT INTO notice (
    user_id,
    content,
    reference_type
) VALUES (
    #{userId},
    #{content},
    #{referenceType}
)

mapper.update:
UPDATE notice
SET is_read = TRUE
WHERE id = #{id}
